import useStats from '../utils/useStats'
import useTime from '../utils/useTime'
import StatBlock from './StatBlock'
import React from 'react'
import styled from 'styled-components'
import Spinner from './Spinner'
import Bar from './Bar'
import LineChart from './LineChart'
import Chart from './Chart'

const vocab = {
  'cases': 'infected',
  'recovered': 'recovered',
  'country': 'country',
  'deaths': 'deaths',
  'todayCases': 'new today',
  'critical': 'critical'
}
const prop = o => k => o[k]
const switchToVocab = word => vocab[word]

const Container = styled.div`
  max-width: 760px;
  margin: 0 auto;
  text-align: center;
`

const Grid = styled.div`
  margin-top: 64px;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 1.5rem;
`

export default function Stats({ url, cn = '', search }) {

  const { stats, error, loading } = useStats(url, cn);

  const worldData = useTime()

  console.log('--------TESTING---------');
  console.log(worldData);

  if (error) return <div> We've encountered an Error. Maybe you typed wrong country</div>
  if (!stats) return <div><Spinner /></div>

  const {
    country,
    cases,
    recovered,
    deaths,
    todayCases,
    critical
  } = stats

  const o = {
    cases, todayCases, recovered, critical, deaths
  }

  const organizeData = d => {
    const mapToKeyword = (k) => ({[switchToVocab(k)]: prop(d)(k)})
    return Object.keys(d).map(mapToKeyword)
  }
  const organizedData = organizeData(o)

  return (
    <Container>

      <h1>Country: {country}</h1>

      <Chart type='bar' info={organizedData} label={country + '\'s Outbreak'} />

      <Grid>
        <StatBlock 
          title="Infected"
          data={cases}
        />
        <StatBlock 
          title="Recovered"
          data={recovered}
        />
        <StatBlock 
          title="Total Deaths"
          data={deaths}
        />
        <StatBlock 
          title="Critical Cases"
          data={critical}
        />
        <StatBlock 
          title="New Cases Today"
          data={todayCases}
        />
      </Grid>

    </Container>
  )
}
