import styled, { withTheme } from 'styled-components'
import Cell from './Cell';


const ScrollTable = styled.div`
  overflow-x: auto;
`

const DataTable = styled.div`
  max-width: 100%;
`

const Table = styled.table`
  position: relative;
  border-collapse: collapse;
  margin: 0 auto;
  border-spacing: 0px;
  background: #fff;
  box-shadow: 0 1px 0 0 rgba(22, 29, 37, 0.05);
  width: 100%;
`

export default ({ headings, theme, isHeb, rows }) => {

  const renderHeadingRow = (_cell, cellIndex) => (
    <Cell
      key={`heading-${cellIndex}`}
      content={ headings[cellIndex] }
      header={true}
    />
  )

  const renderRow = (_row, rowIndex) => {

      return (
        <tr key={`row-${rowIndex}`}>
          {rows[rowIndex].map((_cell, cellIndex) => {
            return (
              <Cell
                key={`${rowIndex}-${cellIndex}`}
                content={rows[rowIndex][cellIndex]}
              />
            )
          })}
        </tr>
      )
    };


    const theadMarkup = (
      <tr key="heading">
        {headings.map(renderHeadingRow)}
      </tr>
    );

    const tbodyMarkup = rows.map(renderRow);

    return (
      <DataTable>
        <ScrollTable>
          <Table>
            <thead>{theadMarkup}</thead>
            <tbody>{tbodyMarkup}</tbody>
          </ Table>
        </ScrollTable>
      </DataTable>
    );


}
