import styled, { withTheme } from 'styled-components'
import { useState, useEffect } from 'react'
import * as S from '../components/S'
import axios from 'axios'

const Wrapper = styled.div`
  margin: 0 auto;
  width: 100%;
  direction: ltr;
`

const Flex = styled.div`
  display: flex;
  flex-direction: column;
  margin: 0 auto;
`

const NewsCard = styled.div`
  margin: 8px 12px;
  display: flex;
  flex-direction: row;
  padding: 16px 6px;
  border: 1px solid gray;
  justify-content: space-between;
`

const LeftWing = styled.div`
  display: flex;
`

const RightWing = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
`

const NewsTime = styled.p`
  font-weight: 700;
`

const NewsBanner = styled.div`
  width: 48px;
  height: 48px;
  display: block;
  border: 1px solid gray;
`

const Col = styled.div`
  display: flex;
  flex-direction: column;
  align-items: start;
`

// const sortIsoDateToRecent = 

const News = ({ theme, isHeb, news }) => {

  console.log(news);

  const newsToTemplate = news => {
    news.map(item => (
      <NewsCard>
        <S.Flex>
          <NewsBanner>
            <img src={item.image} alt={item.title}></img>
          </NewsBanner>
          <S.Flex column alignItems>
            <S.Heading2>{item.title}</S.Heading2>
            <S.Paragraph>{item.contentSnippet}</S.Paragraph>
          </S.Flex>
        </S.Flex>
          <S.Flex justify='flex-end' column>
            <S.Heading2>{item.data}</S.Heading2>
          </S.Flex>
      </NewsCard>
    ))
  }

  return (
    <Wrapper>
      <S.Container>
        <Flex>

          <NewsCard>
            <LeftWing>
              <NewsBanner />
              <Col>
                <S.Heading2>Breaking News</S.Heading2>
                <S.Paragraph>Coronavirus has affectd millions of families leaving mothers and fathers unemployed having to feed their kids mouths</S.Paragraph>
            </Col>
            </LeftWing>
            <RightWing>
              <S.Heading2> 9 days ago </S.Heading2>
            </RightWing>
          </NewsCard>

        </Flex>
      </S.Container>
    </Wrapper>
  )
}

News.getInitialProps = async ctx => {

  const data = await axios.get('https://ncorona.live/api/v1/news/heb')

  console.log(data);
  return { news: data }
}

export default News
