import { Line, defaults } from 'react-chartjs-2';
import 'chartjs-plugin-datalabels';
import styled, { withTheme, keyframes }  from 'styled-components'
import { FadeIn } from './S'

defaults.global.defaultFontFamily = "'PT Sans', sans-serif"

const Div = styled.div`
  margin-top: 32px;
  margin-left: auto;
  margin-right: auto;
  margin-bottom: 48px;
  height: 375px;
  text-align: center;
  width: 80%;
  direction: rtl;
  > * {
    direction: rtl;
  }
  h1 {
    text-shadow: ${({ theme }) => theme.shadows[1]};
  }

  @media (max-width: 768px) {
    height: 40vh;
    margin-bottom: 48px;
    width: 100vw;
    max-width: 100%;
  }
`


const filterBy5 = (x, i) => !(i % 4)
const subtractArray = arr => arr.filter(filterBy5).concat(arr[arr.length -1])

const parseDatasets = (arrayOfSets, fill) => {
  return arrayOfSets.map(set => ({
    ...set,
    data: set.data.length > 30 ? subtractArray(set.data) : set.data,
    fill: fill || false,
  }))
}

const LineChart = ( props, {theme} ) => {

  const data2 = {
    labels: props.labels.length > 30 ? subtractArray(props.labels) : props.labels,
    datasets: parseDatasets(props.data, props.fill)
  };

  const data = canvas => {
    const ctx = canvas.getContext('2d')

    return {
      ...data2,
    }
  }


  var options = {
    responsive: true,
    maintainAspectRatio: false,
    onAnimationComplete: function(){
      this.showTooltip(this.datasets[0].points, true)
    },
    animation: {
        easing: 'easeInOutQuad',
        duration: 150
    },
    scales: {
        xAxes: [{
            gridLines: {
                display: false,
                drawBorder: true,
                drawTicks: true,
                color: 'rgba(0, 0, 0, 0.05)',
                lineWidth: 1,
                zeroLineWidth: 1,
            }
        }],
        yAxes: [{
          ticks: {
            display: true,
            maxTicksLimit: 6,
          },
            gridLines: {
                display: false,
                drawBorder: false,
                // drawBorder: true,
                drawTicks: true,
                color: 'rgba(0, 0, 0, 0.05)',
                lineWidth: 1,
                zeroLineWidth: 1,
            }
        }]
    },
    elements: {
        line: {
            tension: 0.5
        }
    },
    legend: {
      display: props.showLegend!==undefined?false:true,
      position: 'bottom'
    },

    tooltips: {
        enabled: false,
          mode: "x",
          intersect: false,
          custom: (tooltipModel) => {
            if (tooltipModel.opacity === 0) {
              this.hide();
              return;
            }
            const position = this.Chart.chart_instance.canvas.getBoundingClientRect();

            // set position of tooltip
            const left = position.left + tooltipModel.caretX;
            const top = position.top + tooltipModel.caretY;

            // set values
            const date = tooltipModel.dataPoints[0].xLabel;
            const value = tooltipModel.dataPoints[0].yLabel;

            this.setPositionAndData({top, left, date, value});
          },
        },


    // tooltips: {
    //     mode: 'index',
    //     intersect: false,
    //     padding: 15,
    //     titleFontFamily: 'Rubik',
    //     backgroundColor: props.theme.complementary,
    //     titleFontColor: '#f9f9f9',
    // },
    hover: {
      mode: 'index',
      intersect: false,
    },
    plugins: {
       datalabels: {
        display: ctx => {
          let i = ctx.dataIndex
          return i === 0 || i === (ctx.dataset.data.length - 1) || !(i % 4)
        },
        align: ctx => { return ctx.dataIndex === ctx.dataset.data.length -1 ? 'end' : 'end' },
        anchor: 'end',
        padding: {
          left: 0,
          right: -50,
          top: -5,
          bottom: 0
        },
        color: props.theme.color,
         labels: {
           title: { font: { weight: 'bold', style: 'italic', family: 'RubiRubik' } }
         }
       }
    }
};

  return (
      <Div>
          <Line
            label={props.label}
            data={data}
            width={100}
            options={options}
            // height={40}
          />
      </Div>
  )
}

export default withTheme(LineChart)
