import {
  useState,
  useEffect,
  useRef,
  createContext
} from 'react'

export const LocaleContext = createContext()

export const LocaleProvider = (props) => {
  // Default Setting
  const initialState = 'he'

  const [locale, setLocale] = useState(initialState)
  const prev = useRef()


  useEffect(() => {
    if (locale === 'he') {
        const langName = JSON.parse(localStorage.getItem('locale'))
        if (langName) {
          prev.current = langName
          setLocale(locale)
        }
    } else if (locale !== localStorage.getItem('locale')) {
      localStorage.setItem('locale', JSON.stringify(locale))
    }
  }, [locale])

  // sync locale value on client-side route changes
  // React.useEffect(() => {
  //   if (typeof query.lang === 'string' && isLocale(query.lang) && locale !== query.lang) {
  //     setLocale(query.lang)
  //   }
  // }, [query.lang, locale])

  return (
    <LocaleContext.Provider value={[ locale, setLocale ]}>
      {props.children}
    </LocaleContext.Provider>
  )
}
