import {
  useState,
  useEffect,
  useRef,
  createContext
} from 'react'

export const LocaleContext = createContext()

export const LocaleProvider = (props) => {
  // Default Setting
  const initialState = 'he'

  const [ locale, setLocale ] = useState(initialState)
  const prev = useRef()


  prev.current = 'he'

  useEffect(() => {

    const preChanges = localStorage.getItem("locale")
    console.log('pre changes::::::', preChanges);
    localStorage.setItem("locale", locale)
    setLocale(locale)

    // if (prev.current === locale || locale === 'he' ) {
    //   const langName = localStorage.getItem('locale')
    //   if (langName) {
    //     prev.current = langName
    //     setLocale(locale)
    //   } else {
    //     localStorage.setItem("locale", locale)
    //     prev.current = langName
    //   }
    // } else if (prev.current !== locale) {
    //   console.log('writing {LOCALE} to storage');
    //   localStorage.setItem('locale', locale)
    //   prev.current = locale
    // }
  }, [locale])

  return (
    <LocaleContext.Provider value={{ locale, setLocale }} >
      {props.children}
    </LocaleContext.Provider>
  )
}
