import useStats from '../utils/useStats'
import React from 'react';

const Container = styled.div`
  max-width: 760px;
  margin: 0 auto;
  text-align: center;
`

const Grid = styled.div`
  margin-top: 64px;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 1.5rem;
`

export default function Stats(url) {
  const { stats, error, loading } = useStats(url);

  if (error) return <div> We've encountered an Error. Maybe you typed wrong country</div>
  if (!stats) return <div><Spinner /></div>

  const {
    country,
    cases,
    recovered,
    deaths,
    todayCases,
    critical
  } = stats

  console.log(stats)
  return (
    <Container>

      <h1>Country: {country}</h1>
      <Grid>
        <StatBlock 
          title="Infected"
          data={cases}
        />
        <StatBlock 
          title="Recovered"
          data={recovered}
        />
        <StatBlock 
          title="Total Deaths"
          data={deaths}
        />
        <StatBlock 
          title="Critical Cases"
          data={critical}
        />
        <StatBlock 
          title="New Cases Today"
          data={todayCases}
        />
      </Grid>
    </Container>
  )
}
