import styled from 'styled-components'

const Btn = styled.button`
  border-radius: 4px;
  padding: ${props => props.small && '6px 10px' || '10px 14px'};
  font-size: ${props => props.small && '10px' || '16px'};
  background: ${props => props.bgColor && props.bgColor || props.theme.btnBg};
  color: ${props => props.btnColor && props.btnColor || props.theme.btnColor};
  font-family: 'Sans'
`
export default (props) => (
  <Btn onClick={props.onClick}>
    {props.children}
  </Btn>
)
