import useTodayStats from '../utils/useTodayStats'
import useTime from '../utils/useTime'
import StatBlock from './StatBlock'
import React from 'react'
import styled from 'styled-components'
import Spinner from './Spinner'
import Bar from './Bar'
import Chart from './Chart'
import QuickStats from './QuickStats'



const Container = styled.div`
  max-width: 1140px;
  margin: 24px auto 0;
  text-align: center;
  margin-top: 16px;
`

const Flex = styled.div`
  display: flex;
  justify-content: space-evenly;
  background-color: ${props => props.bg || props.theme.bgColor};
  border-radius: 12px;
`

export default function Stats({ cn = '', todayWorld, todayStats, isHeb, yesteryday }) {

  return (
    <Container>
      {
        todayWorld && 
        <>
          <h1 style={{fontSize: '20px', paddingTop: '18px'}}>
            {isHeb && 'נתונים מ' || 'Status for: '}<span>{cn === 'World' && isHeb && 'עולם' || cn}</span>
          </h1>
          <QuickStats country={cn} yday={yesteryday} S={todayWorld} isHeb={isHeb} />
        </>
      }
      {
        todayStats &&
          <QuickStats country={cn} yday={yesteryday} S={todayStats} isHeb={isHeb}/>
      }
    </Container>
  )
}
