import React from 'react'
import axios from 'axios'
import worldTimeData from '../utils/worldTimeData'
import useTime from '../utils/useTime'
import sortForChart from '../utils/sortForChart'
import Chart from './Chart'
import Container from './Container'


class CaseChart extends React.Component {

  state = {
    country: '',
    worldData: [],
    countryData: [],
    worldLabels: [],
    countryLabels: [],
    worldCases: [],
    countryCases: [],
    showWorld: true
  }

  setShowWorld() {
    const showWorld = !this.state.showWorld
    this.setState({ showWorld })
  }

  componentDidMount() {
    const { timeStats } = worldTimeData()
    const { countryStats } = useTime()

    const worldCases = timeStats && timeStats.datasets.filter(a => a.label === 'cases')
    const countryCases = countryStats && countryStats.datasets.filter(a => a.label === 'cases')

    this.setState({
      worldData: timeStats,
      countryData: countryStats,
      worldCases,
      countryCases
    })

  }

  render() {
    return (
      <Container />
    )
  }

}

export default CaseChart
