import CoronaApp from '../components/CoronaApp'

import axios from 'axios'
import { FadeIn } from '../components/S'

import InfoSection from '../components/InfoSection'
import Footer from '../components/Footer'

import Head from 'next/head'


function IndexPage({ isHeb, userLocation, yesterday}) {
  return (
    <>
      <Head>
        <title>nCorona - Novel Coronavirus Statistics & Resources for Coping</title>
      </Head>
      <FadeIn delay="0.5s">
        <CoronaApp isHeb={isHeb} userLocation={userLocation} yesterday={yesterday} />
        <InfoSection />
      </FadeIn>
    </>
  )
}

IndexPage.getInitialProps = async (ctx) => {
  const { data } = await axios.get('https://corona.lmao.ninja/v2/countries/israel?yesterday=true')
  const yesterday = {
    active: data.active,
    cases: data.cases,
    todayCases: data.todayCases,
    recovered: data.recovered,
    deaths: data.deaths,
    country: data.country
  }
  return {yesterday }
}

export default IndexPage
