import App from 'next/app'
import Router from 'next/router'
import Link from 'next/link';
import { useState, useRef } from 'react'
import { ThemeProvider } from 'styled-components'
import axios from 'axios'

import useOutSide from '../utils/useOutSide'
import GlobalStyle from './Global'
import Header from '../components/Header'
import Burger from '../components/Burger';
import Menu from '../components/Menu';
import { MoonIcon, SunIcon, WorldIcon } from '../components/Icons/ThemeIcon'
import { themes } from '../utils/themes'
import * as gtag from '../utils/gtag'


Router.events.on('routeChangeComplete', url => gtag.pageview(url))

const lightTheme = () => ({
  ...themes['common'],
  ...themes['light'],
})

const darkTheme = () => ({
  ...themes['common'],
  ...themes['dark'],
})


async function MyApp ({ Component, pageProps }) {

  async function currentCountry(){
    return await axios
      .get('https://extreme-ip-lookup.com/json/')
      .then(res => res.data.country)
      .catch(e => 'israel')
  }

  const userLocation = currentCountry()


  const [theme, setTheme] = useState(lightTheme())
  const [isHeb, setHeb] = useState(userLocation && (userLocation === 'israel' || userLocation === 'Israel' ? true : false) || 'true')
  const [menuOpen, setOpen] = useState(false)

  const closeMenu     = () => setOpen(false)
  const toggleMenu    = () => setOpen(!menuOpen)
  const toggleLang    = () => {closeMenu(), setHeb(!isHeb)}
  const setDarkTheme  = () => {closeMenu(), setTheme(darkTheme())}
  const setLightTheme = () => {closeMenu(), setTheme(lightTheme())}

  const node          = useRef()
  useOutSide(node, closeMenu)

  const displayLang   = isHeb ? 'English' : '注专转'
  const isLight       = theme.type === 'light'





  return (
    <ThemeProvider theme={theme}>

      <GlobalStyle isHeb={isHeb}/>
      <Header title='nCorona' isHeb={isHeb} >
        <div ref={node}>
          <Burger setOpen={toggleMenu} open={menuOpen} />
          <Menu setOpen={toggleMenu} open={menuOpen} >

            <Link href="/News">
              <a alt="news" onClick={() => closeMenu()}>
                <span>&#128240;</span>
                { isHeb && '砖转' || 'News' }
              </a>
            </Link>

            <Link href="/Statistics">
              <a alt="stats" onClick={() => closeMenu()}>
                <span>	&#128506;</span>
                { isHeb && '转' || 'Data' }
              </a>
            </Link>

            <Link href="/About">
              <a alt="about" onClick={() => closeMenu()}>
                <span>	&#128506;</span>
                { isHeb && '转' || 'About' }
              </a>
            </Link>

    {/*
    // This is Night Mode.. Currently disabled
            <a onClick={isLight ?setDarkTheme : setLightTheme}>
              <span>{ isLight ? '' : ''}</span>
              {
                isLight
                ? (isHeb && '爪 ' || 'Dark Mode')
                : (isHeb && '爪 ' || 'Light Mode')
              }
            </a>
    */}

            <a onClick={toggleLang}>
              <span>&#127760;</span>
              {displayLang}
            </a>

          </Menu>
        </div>

      </Header>
      <Component {...pageProps} isHeb={isHeb} userLocation={typeof userLocation === 'string' && userLocation}  />
    </ThemeProvider>
  )
}


MyApp.getInitialProps = async (appContext) => {
  const appProps = await App.getInitialProps(appContext);
  return { ...appProps }
}

export default MyApp
