import App from 'next/app'
import Router from 'next/router'
import styled, { css, ThemeProvider } from 'styled-components'
import { themes } from '../utils/themes'
import Header from '../components/Header'
import MoonSVG from '../utils/moon.svg'
import SunSVG from '../assets/icons/sun.svg'

import * as gtag from '../utils/gtag'

Router.events.on('routeChangeComplete', url => gtag.pageview(url))

const lightTheme = () => ({
  ...themes['common'],
  ...themes['light'],
})

const darkTheme = () => ({
  ...themes['common'],
  ...themes['dark'],
})

const ThemeIcon = css`
  width: 4rem;
  height: 4rem;
  margin-left: auto;
  cursor: pointer;
  @media (max-width: 768px) {
    width: 3rem;
    height: 3rem;
  }
`

const MoonIcon = styled(MoonSVG)`
  ${ThemeIcon}
  color: lightblue;
`

const SunIcon = styled(SunSVG)`
  ${ThemeIcon}
`


export default class MyApp extends App {

  state = {
    theme: lightTheme(),
    isHeb: true,
  }

  toggleLang =    () => this.setState(prevState => ({ ...prevState, isHeb: !prevState.isHeb }))
  setDarkTheme =  () => this.setState(prevState => ({ ...prevState, theme: darkTheme() }))
  setLightTheme = () => this.setState(prevState => ({ ...prevState, theme: lightTheme() }))

  render() {
    const { theme, isHeb } = this.state
    const displayLang = isHeb ? 'English' : 'עברית'
    const isLight = theme.type === 'light'
    const themeButton = (isLight && <MoonIcon onClick={this.setDarkTheme} />) || <SunIcon onClick={this.setLightTheme} />
    const { Component, pageProps } = this.props

    return (
      <ThemeProvider theme={theme}>
        <Header title='C19.FeeD' isHeb={isHeb}>
          { themeButton }
          <LangBtn onClick={this.toggleLang}>{displayLang}</LangBtn>
        </Header>
        <Component {...pageProps} />
      </ThemeProvider>
    )
  }
}
