import {
  useState,
  useEffect,
  useRef,
  createContext
} from 'react'
// import { useRouter } from 'next/dist/client/router'


export const LocaleContext = createContext()

export const LocaleProvider = ({ lang, children }) => {
  // Default Setting
  const initialState = { locale: 'he', languageName: 'Hebrew' }

  const [locale, setLocale] = useState(initialState.locale)
  // const { query } = useRouter()

  // store the preference
  useEffect(() => {
    if (locale === 'he') {
      const langName = JSON.parse(localStorage.getItem('locale'))
      setLocale(locale)
    } else if (locale !== localStorage.getItem('locale')) {
      localStorage.setItem('locale', JSON.stringify(locale))
    }
  }, [locale])

  // sync locale value on client-side route changes
  // React.useEffect(() => {
  //   if (typeof query.lang === 'string' && isLocale(query.lang) && locale !== query.lang) {
  //     setLocale(query.lang)
  //   }
  // }, [query.lang, locale])

  return <LocaleContext.Provider value={[ locale, setLocale ]}>{children}</LocaleContext.Provider>
}
