import { useState, useEffect } from 'react'
import axios from 'axios'



const capitalize = str => str.charAt(0).toUpperCase() + str.toLowerCase().slice(1)
const parseCn = str => {
  return str === 'usa' || str === 'Usa' || str === 'United States'
    ? 'USA' 
      : str === 'Uk' || str === 'England' || str === 'United Kingdom' || str == 'great britain'
    ? 'UK' 
    : str.toLowerCase();
}

function getCountry(list){
  return function(queryCountry) {
    return list.reduce((a,c) => c.country === queryCountry ? c : a, {})
  }
}

async function currentCountry(){
  return await axios
    .get('https://extreme-ip-lookup.com/json/')
    .then(res => res.data.country)
    .catch(e => 'israel')
}

function useTodayStats(url, country) {
  const [todayStats, setStats] = useState();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);

  useEffect(() => {

    async function fetchData() {

      const { data } = await axios.get('https://corona.lmao.ninja/countries/israel?yesterday=true')
      const good = {
        active: data.active,
        cases: data.cases,
        todayCases: data.todayCases,
        recovered: data.recovered,
        deaths: data.deaths,
        country: data.country
      }

      console.log(good);
      setLoading(true)
      setError()
      let searchCountry = country || await currentCountry();

      const parsedCountry = parseCn(searchCountry);
      const URL = url + 'today/' + parsedCountry.toLowerCase()
      // const URL = url + (country === 'world' ? '' : searchCountry)
      try {
        // const data = await fetch(URL)
        //     .then(res => res.json())
        //     .catch(err => setError(err))
        const { data } = await axios.get(URL)
        const relevant = {
          active: data.active,
          cases: data.cases,
          todayCases: data.todayCases,
          recovered: data.recovered,
          deaths: data.deaths,
          country: data.country
        }
        setStats(relevant)
        setLoading(false)
       } catch(e) {
        setError(e)
      }
    }
    fetchData();
  }, [country])
  return {
    todayStats, loading, error
  }
}

export default useTodayStats;
