import React from 'react';
import {Line} from 'react-chartjs-2';

const filterBy5 = (x, i) => !(i % 2)
const subtractArray = arr => arr.filter(filterBy5)


export default ( props ) => {

  console.log(props);

  const data = {
    labels: props.labels.length > 40 ? subtractArray(props.labels) : props.labels,
    datasets: props.data
  };

  var options = {
    responsive: true,
    maintainAspectRatio: true,
    animation: {
        easing: 'easeInOutQuad',
        duration: 520
    },
    scales: {
        xAxes: [{
            gridLines: {
                color: 'rgba(200, 200, 200, 0.05)',
                lineWidth: 1
            }
        }],
        yAxes: [{
          ticks: {
            maxTicksLimit: 6,
          },
            gridLines: {
                color: 'rgba(200, 200, 200, 0.08)',
                lineWidth: 1
            }
        }]
    },
    elements: {
        line: {
            tension: 0.4
        }
    },
    legend: {
      display: true,
      position: 'bottom'
    },
    tooltips: {
        titleFontFamily: 'Open Sans',
        backgroundColor: 'rgba(20,20,20.7)',
        titleFontColor: '#fff',
        caretSize: 10,
        cornerRadius: 5,
        xPadding: 15,
        yPadding: 15
    }
};

  return (
    <div style={{marginTop: '32px'}}>
      <h1 style={{fontSize: '1.65rem'}}>{props.label}</h1>
      <Line
        label={props.label}
        data={data}
        width={100}
        options={options}
        height={40}
      />
    </div>
  )
}
