const defaultLocale = 'he'

import { useContext } from 'react'
import { LocaleContext } from '../context/LocaleContext'
import { strings } from '../locales/translations'

export default function useTranslation() {
  const { locale } = useContext(LocaleContext)

  const t = (key, key2) => {

    if (!strings[locale][key] || !strings[locale][key][key2]) {
      console.warn(`Translation '${key}' for locale '${locale}' not found.`)
    }

    return typeof key2 === 'string'
      ? strings[locale][key][key2] || strings[defaultLocale][key][key2] || ''
      : strings[locale][key] || strings[defaultLocale][key] || ''

    if (!key2) {
      return strings[locale][key] || strings[defaultLocale][key] || ''
    }
    return strings[locale][key][key2] || strings[defaultLocale][key][key2] || ''
  }

  return {
    t,
    locale
  }
}
