import styled, { css } from 'styled-components'
import MoonSVG from './moon.svg'
import SunSVG from './sun.svg'
import World from '../../public/world.svg'
import CoffeeSVG from '../../public/coffee.svg'

import DownSVG from './down.svg'
import UpSVG from './up.svg'
import DeathSVG from '../../public/death.svg'
import CoughSVG from '../../public/cough.svg'
import LikeSVG from '../../public/like.svg'
import IcuSVG from '../../public/icu.svg'

const ThemeIcon = css`
  width: 2rem;
  height: 2rem;
  margin-left: auto;
  cursor: pointer;
  @media (max-width: 768px) {
    width: 1.5rem;
    height: 1.5rem;
    margin-right: 0;
  }
`

export const UpIcon = styled(UpSVG)`
  ${ThemeIcon}
`

export const DownIcon = styled(DownSVG)`
  ${ThemeIcon}
`

export const DeathIcon = styled(DeathSVG)`
  ${ThemeIcon}
`
export const CoughIcon = styled(CoughSVG)`
  ${ThemeIcon}
`
export const LikeIcon = styled(LikeSVG)`
  ${ThemeIcon}
`
export const IcuIcon = styled(IcuSVG)`
  ${ThemeIcon}
`


export const Coffee = styled(CoffeeSVG)`
  ${ThemeIcon}
  width: 2rem;
  height: 2rem;
`

export const WorldIcon = styled(World)`
  ${ThemeIcon}
  width: 1.5rem;
  height: 1.5rem;
  margin-right: 8px;
`

export const MoonIcon = styled(MoonSVG)`
  ${ThemeIcon}
  color: lightblue;
`

export const SunIcon = styled(SunSVG)`
  ${ThemeIcon}
`
