        const health = data.health
          .map(({subject, link, description, category}) => ({ category, data: [{ subject, link, description }] }))
          .reduce((a,c) => {
            return { category: c.category, data: [ a.data.concat.apply([], c.data) ] }
          },{ initObj })
        console.log(health);
