import { useState, useEffect } from 'react'
import axios from 'axios'

function getCountry(list){
  return function(queryCountry) {
    return list.reduce((a,c) => c.country === queryCountry ? c : a, {})
  }
}

function useStats(url) {
  const [stats, setStats] = useState();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);

  useEffect(() => {

    async function fetchData() {
      setLoading(true)
      const currentCountry = await axios
        .get('https://extreme-ip-lookup.com/json/')
        .then(res => res.data.country)
        .catch(e => console.log('Request failed'))

      const data = await fetch(url)
          .then(res => res.json())
          .then(d => getCountry(d)(currentCountry))
          .catch(err => setError(err))
      setStats(data)
      setLoading(false)
    }
    fetchData();
  }, [])
  return {
    stats, loading, error
  }
}

export default useStats;
