import { useState, useEffect } from 'react'
import axios from 'axios'
import sortForChart from './sortForChart'

const baseURL = `http://46.101.156.51:3003/api/v1/alltime/`

async function currentCountry(){
  return await axios
    .get('https://extreme-ip-lookup.com/json/')
    .then(res => res.data.country)
    .catch(e => 'israel')
}

async function useTime(country, url = baseURL) {
  const [timeStats, setData] = useState();
  const [countryStats, setCountryStats] = useState();
  const [timeLoading, setLoading] = useState(false);
  const [timeError, setError] = useState(false);
  const [geo, setGeo] = useState('')

  useEffect(() => {
    console.log('inside Geo Location Effect');
    (async () => {
      let geoLocation = await currentCountry()
      console.log(geoLocation);
      setGeo(geoLocation)
    })()
  }, [])

  async function fetchData(cn) {
    setLoading(true)
    setError()

    const URL = cn === 'world' ? baseURL : baseURL + country || geo
    const data = await axios.get(URL)
      .then(res => res.data)
      .catch(err => setError(err))
    const timeStats = sortForChart(data);
    console.log(timeStats);
    setData(timeStats)
    setLoading(false)
  }

  // useEffect(() => {
  //   console.log('inside World effect');
  //   fetchData('world')
  // }, [])

  useEffect(() => {
    console.log('inside Country Effect');
    fetchData(country);
  }, [country])

  console.log(countryStats);

  return {
    countryStats, timeStats, timeLoading, timeError
  }
}

export default useTime;
