import { useState, useEffect } from 'react'
import axios from 'axios'
import Container from './Container'
import styled from 'styled-components'

// URL for getting the CSV data.
const baseURL = ''

const Wrapper = styled.div`
  text-align: none;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin-left: 15px;
  margin-right: 15px;
  margin-bottom: 32px;
`

const Section = styled.div`
  border-radius: 4px;
  background: ${props => props.theme.bgColor};
  color: ${props => props.theme.color};
  flex: 0 0 50%;
  max-width: 33%;
  position: relative;
  width: 100%;
  border: 1px solid;
  border-color: ${props => props.theme.statColor};
  margin: 2px;
`

const SectionTitle = styled.h2`
  color: ${props => props.theme.btnColor};
  background: ${props => props.theme.boxBg};
  font-size: 20px;
  padding: 4px 8px;
  border-bottom: 1px solid black;
`

const Link = styled.a`
  text-decoration: none;
  color: ${props => props.theme.statColor};
  font-weight: 700;
  font-size: 18px;
  line-height: 1.5;
  padding: 0 4px;
`

const Desc = styled.p`
  font-size: 16px;
  line-height: 1.22;
  color: ${props => props.theme.color};
  padding: 0 4px;
`

const categories = ['health', 'education', 'activity']

const mapDataToSection = (item, idx) => {
  return (
    <div key={idx}>
      <Link href={item.link}>
        {item.title}
      </Link>
      <Desc>{item.desc}</Desc>
    </div>
  )
}

const renderSection = secData => {
  secData.map((it, ix) => (
    <Section>
      <SectionTitle>
        {it.category}
      </SectionTitle>
      {it.data.map(mapDataToSection)}
    </Section>
  ))
}

// const mapCat = x => ({ x.category, data:   })
// const filtCat = cat => ({ category }) => cat === category
// const filtHealth = filtCat('health')
// const filtEdu = filtCat('education')

export default () => {

  const [data, setData] = useState()
  const [error, setError] = useState()

  useEffect(() => {
    async function fetchData() {
      setError()
      try {
        const initObj = { category: '', data: [] }
        const { health, education } = await axios.get('http://covid19.borisky.me:3003/api/data/resources')
        const d = [health, education]
          .map(categ => categ
            .reduce((a,{ category, link, description, title }) => Object
              .assign(a, {category}, {data: [...a[data], { title, description, link }]})
            ), initObj)

        setData(d)
      } catch(e) {
        setError(e)
      }
    }
    fetchData
  }, [])


  if (error) {return <div> Received Error Fetching Information ... Let Me Know About This: boristofu@gmail.com{ error }</div>}

  console.log(data);

  return (
    <>
      <h1 style={{fontSize: '24px', textAlign: 'center'}}>Useful Information For All</h1>

      <Wrapper>

        {renderSection(data)}

{/*
        <Section>
        <SectionTitle>
          בריאות
        </SectionTitle>
          {
            healthData.map(mapDataToSection)
          }
        </Section>
        <Section>
          <SectionTitle>
            לימודים
          </SectionTitle>
          {
            eduData.map(mapDataToSection)
          }
        </Section>
*/}

      </Wrapper>

    </>
  )

}
