import styled, { withTheme } from 'styled-components'



const Cell = styled.td`
  border: 1px solid #f4f6f8;
  padding: 4px;
  margin-left: 6px;
  margin-right: 6px;
  width: fit-content;
  font-size: 14px;
  font-family: 'Rubik';
  background: ${props => props.theme.primaryLight};
  color: ${props => props.theme.onPrimary};
`

const CellHeader = styled.th`
  ${Cell};
  font-size: 16px;
  padding: 6px 12px;;
  background: ${props => props.theme.complementary};
  color: ${props => props.theme.onPrimary};
`



export default ({ content, header }) => {

  const cellMarkup = header ? (
    <CellHeader>
      {content}
    </CellHeader>
  ) : (
    <Cell>
      {content}
    </Cell>
  );

  return (cellMarkup);
}
