import styled, { withTheme } from 'styled-components'
import { useState, useEffect } from 'react'
import * as S from '../components/S'
import axios from 'axios'
import dateHeb from '../utils/dateHeb'

const Wrapper = styled.div`
  margin: 0 auto;
  width: 100%;
  direction: rtl;
`

const Flex = styled.div`
  display: flex;
  flex-direction: column;
  margin: 0 auto;
`

const NewsCard = styled.div`
  margin: 8px 0;
  display: flex;
  flex-direction: row;
  padding: 16px 6px;
  border: 1px solid gray;
  justify-content: space-between;
`

const LeftWing = styled.div`
  display: flex;
`

const RightWing = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
`

const NewsTime = styled.p`
  font-weight: 700;
`

const NewsBanner = styled.div`
  width: 48px;
  height: 48px;
  display: block;
  border: 1px solid gray;
`

const Col = styled.div`
  display: flex;
  flex-direction: column;
  align-items: start;
`

// const sortIsoDateToRecent = 

const News = ({ theme, isHeb, news }) => {

  console.log(news);

  const newsToTemplate = news => {
    return news.map((item, idx) => (
      <NewsCard key={idx}>
        <S.Flex>
          <NewsBanner>
            <img width="64" height="64" src={item.image} alt={item.title}></img>
          </NewsBanner>
          <S.Flex column alignItems>
            <a style={{textDecoration: 'none'}} href={item.href}>
              <S.Heading2 fontSize='16px'>{item.title}</S.Heading2>
            </a>
            <S.Paragraph fontSize='12px'>{item.description}</S.Paragraph>
          </S.Flex>
        </S.Flex>
          <S.Flex justify='flex-end' column>
            <S.Heading2>{dateHeb(item.date)}</S.Heading2>
          </S.Flex>
      </NewsCard>
    ))
  }

  return (
    <Wrapper>
      <S.Container>
        <Flex>

          {newsToTemplate(news)}

        </Flex>
      </S.Container>
    </Wrapper>
  )
}

News.getInitialProps = async ctx => {

  const { data } = await axios.get('https://ncorona.live/api/v1/news/heb')
  return { news: data.data }
}

export default News
