import { useEffect, useRef, useState, useContext } from 'react'
import Head from 'next/head'
import Link from 'next/link';

import { themes } from '../utils/themes'

// Components
import Burger from '../components/Burger';
import Menu from '../components/Menu';
import Header from '../components/Header'

// Context & Hooks
import { ProxyContext } from '../context/proxy-context'
import { LocaleContext } from '../context/LocaleContext'
import useOutSide from '../hooks/useOutSide'
import useTranslation from '../hooks/useTranslation.js'

import { getInitialLocale } from '../locales/getInitialLocale'


// Functions for changing themes
const lightTheme = () => ({
  ...themes['common'],
  ...themes['light'],
})
const darkTheme = () => ({
  ...themes['common'],
  ...themes['dark'],
})

const langs = [ 'he', 'en' ]

const Layout = ({ children, newProxy }) => {
  const { t } = useTranslation()

  const [proxy,  setProxy ]  = useContext(ProxyContext)
  const { locale, setLocale } = useContext(LocaleContext)


  useEffect(() => {
    let ig = false
    if (locale && !ig) {
      console.log('page refresh');
      let loc = getInitialLocale(proxy && proxy.countryName)
      setLocale(loc)
    }
    return () => { ig: true }
  }, [locale])

  useEffect(() => {
    let ignore = false
    if (newProxy && !ignore) {
      setProxy(newProxy)
    }
    return () => { ignore: true }
  }, [newProxy])

  // manipulate the theme
  const [theme, setTheme] = useState(lightTheme())

  // setting locale. not  sure yet if I use it or context API only
  const [isHeb, setHeb] = useState(proxy && proxy.countryName.toLowerCase() === 'israel' ? true : false)
  const [menuOpen, setOpen] = useState(false)

  const closeMenu     = () => setOpen(false)
  const toggleMenu    = () => setOpen(!menuOpen)
  // const toggleLang    = () => {closeMenu(), setHeb(!isHeb)}
  // const toggleLang    = () => {closeMenu(), setLocale(langs[+!langs.indexOf(locale)])}
  const toggleLang    = () => {closeMenu(); const newLocale = langs[+!langs.indexOf(locale)]; setLocale(newLocale)}
  const node          = useRef()
  useOutSide(node, closeMenu)
  const displayLang   = locale === 'heb' ? 'English' : 'עברית'

  return (
    <>
      <Head>
        <title>{t('metaTitle')}</title>
        <meta name="description" content={t('metaKey')}/>
      </Head>
      <>
        <Header title="nCorona" isHeb={isHeb} >

          <div ref={node}>

            {/*-------- /// Burger Menu & Menu /// --------- */}

            <Burger setOpen={toggleMenu} open={menuOpen} />
            <Menu setOpen={toggleMenu} open={menuOpen} >

            {/*-------- /// NAV LINKS /// --------- */}

              <Link href="/News">
                <a alt="news" onClick={() => closeMenu()}>
                  <span>&#128240;</span>
                  {t('links')('news')}
                </a>
              </Link>

              <Link href="/Statistics">
                <a alt="stats" onClick={() => closeMenu()}>
                  <span>	&#128506;</span>
                  {t('links')('statistics')}
                </a>
              </Link>

              <Link href="/About">
                <a alt="about" onClick={() => closeMenu()}>
                  <span>	&#128506;</span>
                  {t('links')('about')}
                </a>
              </Link>

              {/*
                    // MEANT FOR NIGHT MODE -
                    // This is Night Mode.. Currently disabled
                      <a onClick={isLight ?setDarkTheme : setLightTheme}>
                        <span>{ isLight ? '🌒' : '🌞'}</span>
                        {
                          isLight
                          ? (isHeb && 'מצב לילה' || 'Dark Mode')
                          : (isHeb && 'מצב יום' || 'Light Mode')
                        }
                      </a>
              */}

              <a onClick={toggleLang}>
                <span>&#127760;</span>
                {displayLang}
              </a>

            </Menu>
          </div>


        </Header>
        {children}
      </>
    </>
  )
}

export default Layout
