import { useState, useEffect } from 'react';

function useStats() {
  const [stats, setStats] = useState();
  useEffect(() => {
    async function fetchData() {
      console.log('fetching data...');
      const data = await 
        fetch('https://covid19.mathdro.id/api/countries/canada')
        .then(res => res.json());
      setStats(data);
    }
    fetchData();
  }, [])
  return stats
}

function Stats() {
  const stats = useStats();

  if (!stats) return <div>Loading...</div>

  const confirmed = stats.confirmed.value
  const recovered = stats.recovered.value
  const deaths = stats.deaths.value

  console.log(stats)

  return (
    <div>
      <ul>
        <li>Infected: {confirmed}</li>
        <li>Recovered: {recovered}</li>
        <li><strong>Fatalities: {deaths}</strong></li>
      </ul>
    </div>
  )
}

export default function IndexPage() {
  return (
    <div>
      <Stats />
    </div>
  )
}
