import useStats from '../utils/useStats'
import StatBlock from '../components/StatBlock'
import Spinner from '../components/Spinner'
import styled from 'styled-components'
import { createGlobalStyle } from 'styled-components'
import GlobalStyle from './Global'

// Seems like it's not UP TO DATE
// fetch('https://covid19.mathdro.id/api/countries/Italy')



// const url = 'https://corona.lmao.ninja/countries'
const url = 'https://corona.lmao'

const Input = styled.input`
  margin: 54px 0 auto;
  outline: none;
  max-width: 420px;
  text-align: center;
  border-radius: 6px;
  background: #f1f1f1;
  font-size: 1.1rem;
  text-transform: uppercase;
`

const Container = styled.div`
  max-width: 760px;
  margin: 0 auto;
  text-align: center;
`

const Grid = styled.div`
  margin-top: 64px;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 1.5rem;
`

function Stats() {
  const { stats, error, loading } = useStats(url);

  if (error) return <div> We've encountered an Error. Maybe you typed wrong country</div>
  if (!stats) return <div><Spinner /></div>

  const {
    country,
    cases,
    recovered,
    deaths,
    todayCases,
    critical
  } = stats

  console.log(stats)
  return (
    <Container>

      <h1>Country: {country}</h1>
      <Grid>
        <StatBlock 
          title="Infected"
          data={cases}
        />
        <StatBlock 
          title="Recovered"
          data={recovered}
        />
        <StatBlock 
          title="Total Deaths"
          data={deaths}
        />
        <StatBlock 
          title="Critical Cases"
          data={critical}
        />
        <StatBlock 
          title="New Cases Today"
          data={todayCases}
        />
      </Grid>
    </Container>
  )
}

const Search = props => {
  const [searchValue, setSearch] = useState('');

  function handleChange(e) {
    setSearch(e.target.value)
  }
  function handleSubmit(s) {
    s.preventDeafult()

  }
  return (
    <form onSubmit={handleSubmit}>
      <label>
        Search For Country:
        <Input />
      </label>
      <input type="submit" value="submit" />
    </form>
  )
}

export default function IndexPage() {
  return (
    <Container>
      <GlobalStyle />
      <Stats />
      <Input onSubmit/>
    </Container>
  )
}
