import { useState, useEffect } from 'react';

function useStats() {
  const [stats, setStats] = useState();
  useEffect(() => {
    async function fetchData() {
      console.log('fetching data...');
      const data = await fetch('https://covid19.mathdro.id/api/countries/israel').then(data => data.json());
      setStats(data);
    }
    fetchData();
  }, [])
  return stats
}

function Stats() {
  const stats = useStats();
  let confirmed,recovered,deaths
  // const confirmed = stats.confirmed.value
  // const recovered = stats.recovered.value
  // const deaths = stats.deaths.value
  console.log(stats)
  // const { confirmed, recovered, deaths } = stats;
  return (
    <div>
      <ul>
        <li>Infected: ${confirmed}</li>
        <li>Recovered: ${recovered}</li>
        <li><strong>Fatalities: ${deaths}</strong></li>
      </ul>
    </div>
  )
}

export default function IndexPage() {
  return (
    <div>
      <Stats />
    </div>
  )
}
