import styled, { withTheme } from 'styled-components'
import Container from './Container'
import Button from './Button'
import Coffee from '../public/coffee.svg'
import Link from 'next/link'

const CoffeeIcon = styled(Coffee)`
  width: 1.5rem;
  height: 1.5rem;
  margin: 0px 6px 0 0;
  display: inline-block;
  font-size: 14px;
  padding-top: 4px;
`

const Wrap = styled.div`
  direction: ltr;
  position: relative;
  height: 82px;
  width: 100%;
  background: linear-gradient(45deg, #5c0434, #000000d6);
  border-bottom: 1px solid darkblue;
  margin: 0 auto;
  margin-bottom: 48px;
`

const MainHeader = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  @media (max-width: 768px) {
    margin-left: 12px;
    margin-right: 12px;
  }
`

const Nav = styled.nav`
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  @media (max-width: 768px) {
    margin-top: 10px;
  }
`

const List = styled.ul`
  display: flex;
  align-items: center;
`

const Header = (props, { title, children, isHeb }) => {
  return (
    <Wrap>
      <div style={{ maxWidth: '820px', margin: '0 auto' }}>
        <MainHeader>
          <nav className="nav">
            <ul className="nav__list">
              <h1>{'C19feed'}</h1>
              <li className="nav__item">
                <Link href='/Coffee'>
                  <Button small bgColor='#c82333' link>
                  <CoffeeIcon />
                    Buy Me A Coffix
                  </Button>
                </Link>
              </li>
            </ul>

            <div className="nav__side"> { children } </div>

          </nav>
        </MainHeader>
      </div>
      <style jsx>{`
        h1 {
          margin-top: 20px;
          color: #e6e6e6;
          font-size: 3.5rem;
          font-family: 'Montserrat';
          text-align: center;
          text-shadow: 0 1px 35px rgba(5, 35, 10, 0.2);
        }

        nav {
          display: flex;
          align-items: center;
          justify-content: space-between;
          width: 100%;
        }


        .nav__list {
          display: flex;
          align-items: center;
        }

        .nav__item {
          margin-top: 20px;
          margin-left: 40px;
          list-style: none;
          display: flex;
          justify-content: space-between;
          align-items: center;
        }

        .nav__side {
          display: flex;
          margin-top: 20px;
        }

        @media (max-width: 768px) {
          h1 {
            font-size: 2.5rem;
          }
          .nav__item {
            margin-left: 12px;
          }
          nav {
            margin-top: 10px;
          }
        }

      `}</style>

    </Wrap>
  )
}

export default withTheme(Header)
