import { useState, useEffect } from 'react'
import Input from './Input'
import Stats from './Stats'
import Header from './Header'
import useTime from '../utils/useTime'
import useTodayStats from '../utils/useTodayStats'
import Chart from './Chart'
import Container from './Container'
import HeaderDescription from './HeaderDescription'
import sortForChart from '../utils/sortForChart'
import mapDataForTodayGraph from '../utils/mapDataForTodayGraph'
import Button from './Button'

export default () => {

  const [showWorld, setTimeData] = useState(true)
  const [country, setCountry] = useState('')
  const [inputValue, setInput] = useState('')


  const url = 'http://covid19.borisky.me:3003/api/v1/'

  const { todayStats } = useTodayStats(url, country)

  const {countryStats, error, loading } = useTime(country)

  const todayStatsSorted = todayStats && 
    {
      cases: todayStats.cases,
      todayCases: todayStats.todayCases,
      recovered: todayStats.recovered,
      deaths: todayStats.deaths
    } || ''

  const changeData = k => setTimeData(k)
  const handleChange = e => setInput(e.target.value)
  const handleSubmit = e => {
    e.preventDefault();
    setCountry(inputValue)
  }

  if (loading) return <div> Loading....</div>
  if (error) return <div> We've encountered an Error. Maybe you typed wrong country</div>

  const worldCases = timeStats && timeStats.datasets.filter(a => a.label === 'cases')

  const countryCases = countryStats && countryStats.datasets.filter(a => a.label === 'cases')

  console.log(countryStats && countryCases);

  return (
    <div>
      <Header>Covid19 Feed</Header>
      <HeaderDescription />

      <Stats
        cn={country || todayStats && todayStats.country}
        // timeData={timeStats}
        todayStats={todayStatsSorted}
      />
      <Container>

      <div style={{display: 'flex', justifyContent: 'center', padding: '34px 32px'}}>
        <Button onClick={() => changeData(true)}>World</Button><span> | </span>
        <Button onClick={() => changeData(false)}>{country || 'Israel'}</Button>
      </div>

      {/* Time Charts. Separate For Each Dataset (Cases, Deaths, New Cases) */}

      {/* 
      */}
      {
        timeStats &&
          (
            <Chart 
              type='line'
              labels={showWorld && timeStats.labels || countryStats.labels}
              data={
                showWorld
                  && worldCases
                  || countryCases
              }
              dataZ={showWorld 
                  && timeStats.datasets.filter(x=>x.label === 'cases')
                  || countryStats.datasets.filter(x=>x.label === 'cases')}
              label='Infection Growth - Country/World'
            />
          ) || <div> Loading Charts.............. </div>
      }
      </Container>

      <form 
        style={{ maxWidth: '520px', margin: '54px auto'}}
        onSubmit={handleSubmit}>

        <label style={{fontSize: '16px'}}>
          Search For Country:
        </label>

        <div style={{ display: 'flex', margin: '0 auto' }}>
          <Input type="text" value={inputValue} onChange={handleChange} />
          <Input type="submit" value="submit" btn={true}/>
        </div>
      </form>

    </div>
  )


}
