import { Line, defaults } from 'react-chartjs-2';
import { useRef } from 'react'
import 'chartjs-plugin-datalabels';
import styled, { withTheme, keyframes }  from 'styled-components'
import { FadeIn } from './S'

defaults.global.defaultFontFamily = "'PT Sans', sans-serif"

const Div = styled.div`
  margin-top: 32px;
  margin-left: auto;
  margin-right: auto;
  margin-bottom: 48px;
  height: 375px;
  text-align: center;
  width: 80%;
  direction: rtl;
  > * {
    direction: rtl;
  }
  h1 {
    text-shadow: ${({ theme }) => theme.shadows[1]};
  }

  @media (max-width: 768px) {
    height: 40vh;
    margin-bottom: 48px;
    width: 100vw;
    max-width: 100%;
  }
`


const filterBy5 = (x, i) => !(i % 4)
const subtractArray = arr => arr.filter(filterBy5).concat(arr[arr.length -1])

const parseDatasets = (arrayOfSets, fill) => {
  return arrayOfSets.map(set => ({
    ...set,
    data: set.data.length > 30 ? subtractArray(set.data) : set.data,
    fill: fill || false,
  }))
}

const LineChart = ( props, {theme} ) => {

  const chartRef = useRef()

  const data2 = {
    labels: props.labels.length > 30 ? subtractArray(props.labels) : props.labels,
    datasets: parseDatasets(props.data, props.fill)
  };

  const data = canvas => {
    const ctx = canvas.getContext('2d')

    return {
      ...data2,
    }
  }


  var options = {
    responsive: true,
    maintainAspectRatio: false,
    onAnimationComplete: function(){
      this.showTooltip(this.datasets[0].points, true)
    },
    layout: {
      padding: {
        left: 5,
        right: 15,
        top: 15,
        bottom: 15
      }
    },
    animation: {
        easing: 'easeInOutQuad',
        duration: 150
    },
    scales: {
        xAxes: [{
            gridLines: {
                display: false,
                drawBorder: true,
                drawTicks: true,
                color: 'rgba(0, 0, 0, 0.05)',
                lineWidth: 1,
                zeroLineWidth: 1,
            }
        }],
        yAxes: [{
          ticks: {
            display: true,
            maxTicksLimit: 6,
          },
            gridLines: {
                display: false,
                drawBorder: false,
                // drawBorder: true,
                drawTicks: true,
                color: 'rgba(0, 0, 0, 0.05)',
                lineWidth: 1,
                zeroLineWidth: 1,
            }
        }]
    },
    elements: {
        line: {
            tension: 0.5
        }
    },
    legend: {
      display: props.showLegend!==undefined?false:true,
      position: 'bottom'
    },


    tooltips: {
        // Disable the on-canvas tooltip
        enabled: false,

        custom: function(tooltipModel) {
            // Tooltip Element
            var tooltipEl = document.getElementById('chartjs-tooltip');

            // Create element on first render
            if (!tooltipEl) {
                tooltipEl = document.createElement('div');
                tooltipEl.id = 'chartjs-tooltip';
                tooltipEl.innerHTML = '<table></table>';
                document.body.appendChild(tooltipEl);
            }

            // Hide if no tooltip
            if (tooltipModel.opacity === 0) {
                tooltipEl.style.opacity = 0;
                return;
            }

            // Set caret Position
            tooltipEl.classList.remove('above', 'below', 'no-transform');
            if (tooltipModel.yAlign) {
                tooltipEl.classList.add(tooltipModel.yAlign);
            } else {
                tooltipEl.classList.add('no-transform');
            }

            function getBody(bodyItem) {
                return bodyItem.lines;
            }

            // Set Text
            if (tooltipModel.body) {
                var titleLines = tooltipModel.title || [];
                var bodyLines = tooltipModel.body.map(getBody);

                var innerHtml = '<thead>';

                titleLines.forEach(function(title) {
                    innerHtml += '<tr><th>' + title + '</th></tr>';
                });
                innerHtml += '</thead><tbody>';

                bodyLines.forEach(function(body, i) {
                    var colors = tooltipModel.labelColors[i];
                    var style = 'background:' + colors.backgroundColor;
                    style += '; border-color:' + colors.borderColor;
                    style += '; border-width: 2px';
                    var span = '<span style="' + style + '"></span>';
                    innerHtml += '<tr><td>' + span + body + '</td></tr>';
                });
                innerHtml += '</tbody>';

                var tableRoot = tooltipEl.querySelector('table');
                tableRoot.innerHTML = innerHtml;
            }

            // `this` will be the overall tooltip

            // Display, position, and set styles for font
            tooltipEl.style.opacity = 1;
            tooltipEl.style.position = 'absolute';
            tooltipEl.style.left = position.left + window.pageXOffset + tooltipModel.caretX + 'px';
            tooltipEl.style.top = position.top + window.pageYOffset + tooltipModel.caretY + 'px';
            tooltipEl.style.fontFamily = tooltipModel._bodyFontFamily;
            tooltipEl.style.fontSize = tooltipModel.bodyFontSize + 'px';
            tooltipEl.style.fontStyle = tooltipModel._bodyFontStyle;
            tooltipEl.style.padding = tooltipModel.yPadding + 'px ' + tooltipModel.xPadding + 'px';
            tooltipEl.style.pointerEvents = 'none';
            const padding = 10;
            const position = this._chart.canvas.getBoundingClientRect();

            // Ensure Tooltip stays in view at far-left and far-right edges

            const toolWidth = tooltipModel.width;
            const graphWidth = lineGraph.width;
            const caretPosition = tooltipModel.caretX;
            const maxPosition = graphWidth - toolWidth;
            const normalizedPosition = caretPosition + position.left;

            if (caretPosition >= maxPosition) {
                //Move tooltip left by the excess amount with padding
                const xDif = caretPosition - maxPosition;
                tooltipEl.style.left = normalizedPosition - (xDif + padding) + 'px';
            } else {
                //Return normal tooltip position
                tooltipEl.style.left = `${normalizedPosition}px`;
            }

            //Ensure Tooltip stays in view at top and bottom most positions

            const toolHeight = tooltipModel.height;
            const graphHeight = lineGraph.height;
            const caretYPosition = tooltipModel.caretY;
            const maxYPosition = graphHeight - toolHeight;
            const normalizedYPosition = caretYPosition + position.top;

            if (caretYPosition >= maxYPosition) {
                const yDif = caretYPosition - maxYPosition;
                tooltipEl.style.top = normalizedYPosition - (yDif + padding) + "px";
            } else {
                tooltipEl.style.top = `${normalizedYPosition}px`;
            }


        }
    },




    // tooltips: {
    //     mode: 'index',
    //     intersect: false,
    //     padding: 15,
    //     titleFontFamily: 'Rubik',
    //     backgroundColor: props.theme.complementary,
    //     titleFontColor: '#f9f9f9',
    // },
    hover: {
      mode: 'index',
      intersect: false,
    },
    plugins: {
       datalabels: {
        display: ctx => {
          let i = ctx.dataIndex
          return i === 0 || i === (ctx.dataset.data.length - 1) || !(i % 4)
        },
        align: ctx => { return ctx.dataIndex === ctx.dataset.data.length -1 ? 'end' : 'end' },
        anchor: 'end',
        padding: {
          left: 0,
          right: -50,
          top: -5,
          bottom: 0
        },
        color: props.theme.color,
         labels: {
           title: { font: { weight: 'bold', style: 'italic', family: 'RubiRubik' } }
         }
       }
    }
}

  return (
      <Div>
          <Line
            label={props.label}
            data={data}
            width={100}
            options={options}
            // height={40}
          />
      </Div>
  )
}

export default withTheme(LineChart)
