// import data from CSV ?

import { useState, useEffect } from 'react'
import axios from 'axios'
import Container from './Container'
import styled from 'styled-components'

// URL for getting the CSV data.
const baseURL = ''

const Section = styled.div`
  background: ${props => props.theme.bgColor};
  color: ${props => props.theme.color};
`

// make different sections by topics. i.e  Health. Corona. || Activities || Free COntent/ COurses etc..

export default () => {

  const healthData = [
    {
      title: 'עשרת הדברות בקורונה',
      link: 'http://covid19.borisky.me',
      desc: 'הוראות והנחיות שחשוב להקפיד על מנת לשמור על עצמכם בסיכון נמוך של הידבקות מהקורונה.'
    },
    {
      title: 'ניופץ מיתוסים בנוגע לקורונה',
      link: 'http://mythbustsers.com',
      desc: 'כנסו לקרוא על מיתוסים נפוצים שבטח שמעתים מקרוביכם ואפילו מפרופסורים שונים בטלויזיה'
    }
  ]

  const eduData = [
    {
      title: 'FreeCodeCamp - Web Development Courses',
      link: 'http://freecodecamp.com',
      desc: 'Free online web development course that takes the student from absolutely ZERO knowledge to actually being able to code real and useful websites. Like this ONE!'
    },
    {
      title: 'Coursera',
      link: 'https://coursera.com',
      desc: 'Coursera offers free courses as well as paid courses. very good quality for both contents'
    }
  ]

  // const [healthData, setHealthData] = useState()
  // const [eduData, setEduData] = useState()
  const [otherData, setOtherData] = useState()

  return (
    <>
      <Container>
        <h1>Useful Information For All</h1>
        <div>
          <h2>
            בריאות
          </h2>
          {
            healthData.map((s,i) => {
              return (
                <div key={i}>
                  <a href={s.link}>
                    <h3>{s.title}</h3>
                  </a>
                  <p>{s.desc}</p>
                </div>
              )
            })
          }
        </div>
        <h2>
          לימודים
        </h2>
        {
          eduData.map((s,i) => (<div key={i}>{s.desc}</div>))
        }
        <div>
        </div>

      </Container>
    </>
  )

}
