import styled from 'styled-components'
import { Container } from './S'
import { DownIcon, UpIcon } from './Icons/ThemeIcon'

const formatNumber = x => x
  .toString()
  .split('')
  .reverse()
  .map((x,i) => i % 3 === 0 ? x + ',' : x)
  .reverse()
  .join('')
  .slice(0, -1)

const Column = styled.div`
  display: flex;
  flex-flow: column wrap;
  direction: rtl;
  align-items: start;
  justify-content: space-evenly;
  justify-content: space-evenly;

  width: 100%;
  background: #fafafa;
  padding: 16px 3em 12px;
  border-radius: 2px;

  @media (min-width: 660px) {
    box-shadow: none;
    border: 1px solid #e4e7eb;
  }

  &:last-child {
  }

`

const InRow = styled.div`
  display: flex;
  flex-flow: row wrap;
  font-weight: 700;
  color: ${props => props.theme.primaryColor};
  justify-content: ${props => props.justify || 'space-between'};
  width: 100%;
  line-height: 1.65;
  // box-shadow: ${props => props.theme.shadows[1]};
`

const TheRow = styled.div`
  display: flex;
  flex-flow: column wrap;
  justify-content: space-between;
  @media (min-width: 660px) {
    flex-flow: row;
  }
`

const Percentage = styled.h1`
  font-size: 14px;
  font-weight: 500;
  color: ${props => props.theme.text.secondary};
  align-self: flex-end;
  padding-bottom: 2px;
  border-radius: 12px;
  background: #DFF7EC;
  padding: 0.5em 1em;
  span {
    font-family: 'Verdana';
    background: #DFF7EC;
    color: ${props => props.theme.primaryLight};
  }

`

const Header = styled.div`
  font-size: 20px;
  font-weight: 500;
  color: ${props => props.theme.text.primary};
  width: auto;
  letter-spacing: 0.1px;
  line-height: 1.85;
  @media (min-width: 768px) {
    font-size: 14x;
  }
`

const Number = styled.h1`
  font-size: 24px;
  font-weight: 700;
  color: ${props => props.theme.primaryVariant};
  letter-spacing: 1.2px;
  font-family: 'Montserrat';

  span {
    font-size: 12px;
    font-family: 'Arial';
    color: ${props => props.theme.text.disabled};
    letter-spacing: 0px;
    color: #717171;
    margin-left: 1em;
    margin-right: 1em;
  }

  @media (min-width: 768px) {
    font-size: 16px;
  }

`

const Row = styled.div`
  display: flex;
  flex-wrap: wrap;
  margin-right: -15px;
  margin-left: -15px;
`

const Col = styled.div`
  text-align: center;
  padding: 8px 10px;
  font-weight: 400;
  flex: 1 1 20%;
  max-width: 20%;
  position: relative;
  width: 100%;
  display: flex;
  flex-direction: column;
  background: ${props => (props.bg && props.theme.general[props.bg]) || props.theme.primaryColor};
  color: ${props => props.theme.onPrimary};
  justify-content: center;
  @media (max-width: 768px) {
    padding: 8px 16px;
  }
`

const Country = styled.h3`
  font-size: 16px;
  font-weight: bold;
  align-self: center;
  color: ${props => props.theme.onPrimary};
  @media (max-width: 768px) {
    font-size: 12px;
  }
`

const Div = styled.div`
  box-shadow: ${props => props.theme.shadows[2]};
  border-radius: 6px;
  margin-bottom: 16px;
`

const isPercentDown = x => x < 0
const showPercent = x => x !== 0
const calcDiff = current => prev => current !== 0 ? ((current - prev) / prev * 100).toFixed(2) : 0
// Percentage of increase = |100 - 150|/100 = 50/100 = 0.5 = 50%

export default ({ S, country, isHeb, yday}) => {

  console.log(
    calcDiff(S.cases)(yday.cases)
  );

  return (
    <Container>


    <TheRow>

      <Column>
        <Header>
          {isHeb ? 'מאומתים' : 'Confirmed'}
        </Header>
        <InRow>
          <Number>
            {formatNumber(S.cases)}
            <span>מ {formatNumber(yday.cases)} ...</span>
          </Number>
          <br />
          <Percentage>
            <span>{ calcDiff(S.cases)(yday.cases) < 0 && "\u2B07" || '\u2B06' }</span>
            { calcDiff(S.cases)(yday.cases)} %
        </Percentage>
        </InRow>
      </Column>

    <Column>
      <Header>
        {isHeb ? 'החלימו' : 'Recovered'}
      </Header>
      <InRow>
        <Number>
        {formatNumber(S.recovered)}
        <span>מ{formatNumber(yday.recovered)}...</span>
    </Number>
        <br />
        <Percentage>
          <span>{ calcDiff(S.recovered)(yday.recovered) < 0 && "\u2B07" || '\u2B06' }</span>
          { calcDiff(S.recovered)(yday.recovered)} %
        </Percentage>
      </InRow>
    </Column>

    <Column>
      <Header>
        {isHeb ? 'נפטרו' : 'Deaths'}
      </Header>
      <InRow>
        <Number>
          {formatNumber(S.deaths)}
          <span>מ {formatNumber(yday.deaths)}...</span>
        </Number>
        <br />
        <Percentage>
          <span down={false}>{ calcDiff(S.deaths)(yday.deaths) < 0 && "\u2B07" || '\u2B06' }</span>
          { calcDiff(S.deaths)(yday.deaths)} %
        </Percentage>
      </InRow>
    </Column>

    <Column>
      <Header>
        {isHeb ? 'מקרים חדשים' : 'Today Cases'}
      </Header>
      <InRow>
        <Number>
          {formatNumber(S.todayCases)}
          <span>מ {formatNumber(yday.todayCases)}...</span>
        </Number>
        <br />
        <Percentage>
          <span style={{
            color: calcDiff(S.todayCases)(yday.todayCases) < 0 ? 'green' : 'red'
          }}>{ calcDiff(S.todayCases)(yday.todayCases) < 0 && "\u2B07" || '\u2B06' }</span>
          { -calcDiff(S.todayCases)(yday.todayCases)} %
      </Percentage>
      </InRow>
    </Column>


    </TheRow>


      <Div>
        <Row>
          <Col style={{ borderRight: 'none', borderTopRightRadius: '5px', borderBottomRightRadius: '5px' }}>
            <Country> {country} </Country>
          </Col>
          <Col bg='successDefault' >
            <Country>
              <small>{isHeb ? 'מאומתים' : 'Confirmed'}</small>
              <br />
              {S.cases}
            </Country>
          </Col>
          <Col bg='analogous'>
            <Country>
              <small>{isHeb ? 'החלימו' : 'Recovered'}</small>
              <br />
              {S.recovered}
            </Country>
          </Col>
          <Col bg='error'>
            <Country>
              <small>{ isHeb ? 'נפטרו' : 'Deaths' }</small>
              <br />
              {S.deaths}
          </Country>
          </Col>
          <Col bg='error2' style={{ borderLeft: 'none', borderTopLeftRadius: '5px', borderBottomLeftRadius: '5px' }}>
            <Country>
              <small>{isHeb ? 'מקרים חדשים' : 'Today Cases'}</small>
              <br />
              {S.todayCases}
          </Country>
          </Col>
        </Row>

      </Div>
    </Container>
  )

}
