import { useState, useEffect } from 'react'
import axios from 'axios'
import Container from './Container'
import styled from 'styled-components'

// URL for getting the CSV data.
const baseURL = ''

const Wrapper = styled.div`
  text-align: none;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  margin-left: 15px;
  margin-right: 15px;
  margin-bottom: 32px;
`

const Section = styled.div`
  border-radius: 4px;
  background: ${props => props.theme.bgColor};
  color: ${props => props.theme.color};
  flex: 0 0 50%;
  max-width: 33%;
  position: relative;
  width: 100%;
  border: 1px solid;
  border-color: ${props => props.theme.statColor};
  margin: 2px;
`

const SectionTitle = styled.h2`
  color: ${props => props.theme.btnColor};
  background: ${props => props.theme.boxBg};
  font-size: 20px;
  padding: 4px 8px;
  border-bottom: 1px solid black;
`

const Link = styled.a`
  text-decoration: none;
  color: ${props => props.theme.statColor};
  font-weight: 700;
  font-size: 18px;
  line-height: 1.5;
  padding: 0 4px;
`

const Desc = styled.p`
  font-size: 16px;
  line-height: 1.22;
  color: ${props => props.theme.color};
  padding: 0 4px;
`

const categories = ['health', 'education', 'activity']


const reduceToCat = (a, { category, ...x }) => ({ category, data: [].concat.apply({...x}, a.data) })

const initObj = { category: '', data: [] }

const mapCat = ({subject, link, description, category}) => ({ category, data: [{ subject, link, description }] })

const mapToCat = i => i.reduce(reduceToCat, initObj)

const mapDataToSection = (item, idx) => {
  return (
    <div key={idx}>

      <Desc>{item.desc}</Desc>
    </div>
  )
}

const renderSection = secData => {
  return secData.map((it, ix) => (
    <Section>
      <SectionTitle>
        {it.category}
      </SectionTitle>
      {it.data.map(mapDataToSection)}
    </Section>
  ))
}

export default () => {

  const [data, setData] = useState()
  const [error, setError] = useState()

  useEffect(() => {
    async function fetchData() {
      setError()
      try {


        const D = await axios.get('https://covid19.borisky.me/api/data/resources')
        console.log(D);

        const d = Object
                    .keys(D)
                    .map(o => D[o].map(mapCat).reduce(reduceToCat))

        // const { data } = await axios.get('https://covid19.borisky.me/api/data/resources')
        // const health = data.health
        //   .map(mapCat)
