// Seems like it's not UP TO DATE
// fetch('https://covid19.mathdro.id/api/countries/Italy')

function getCountry(list){
  return function(queryCountry) {
    return list.reduce((a,c) => c.country === queryCountry ? c : a, {})
  }
}

function useStats() {
  const [stats, setStats] = useState();
  useEffect(() => {
    async function fetchData() {
      console.log('fetching data...')
      const data = await
        fetch('https://corona.lmao.ninja/countries')
        .then(res => res.json())
        .then(d => getCountry(d)("Israel"))
      setStats(data)
    }
    fetchData();
  }, [])
  return stats
}

function Stats() {
  const stats = useStats();

  if (!stats) return <div>Loading...</div>

  const confirmed = stats.confirmed
  const recovered = stats.recovered
  const deaths = stats.deaths
  console.log(stats)
  return (
    <div>
    </div>
  )
}

export default function IndexPage() {
  return (
    <div>
      <Stats />
    </div>
  )
}
