import { useState, useEffect } from 'react';

// Seems like it's not UP TO DATE
// fetch('https://covid19.mathdro.id/api/countries/Italy')

function getCountry(list){
  return function(queryCountry) {
    return list.reduce((a,c) => c.country === queryCountry ? c : a, {})
  }
}

const StatBlock = ({ title, data }) => {
  return (
    <div className="statBlock">
      <h3>{title}:</h3>
      <span>{data}</span>
    </div>
  )
}


function useStats() {
  const [stats, setStats] = useState();
  useEffect(() => {
    async function fetchData() {
      console.log('fetching data...')
      const data = await
        fetch('https://corona.lmao.ninja/countries')
        .then(res => res.json())
        .then(d => getCountry(d)("Israel"))
      setStats(data)
    }
    fetchData();
  }, [])
  return stats
}

function Stats() {
  const stats = useStats();

  if (!stats) return <div>Loading...</div>

  const {
    cases,
    recovered,
    deaths,
    todayCases,
    critical
  } = stats

  console.log(stats)
  return (
    <div>
      <StatBlock 
        title="Infected"
        data={cases}
      />
      <StatBlock 
        title="Recovered"
        data={recovered}
      />
      <StatBlock 
        title="Total Deaths"
        data={deaths}
      />
      <StatBlock 
        title="Critical Cases"
        data={critical}
      />
      <StatBlock 
        title="New Cases Today"
        data={todayCases}
      />
    </div>
  )
}

export default function IndexPage() {
  return (
    <div>
      <Stats />
    </div>
  )
}
