import App from 'next/app'
import Router from 'next/router'
import styled, { css, ThemeProvider } from 'styled-components'
import { themes } from '../utils/themes'
import Header from '../components/Header'
import MoonSVG from '../utils/moon.svg'
import SunSVG from '../assets/icons/sun.svg'

import * as gtag from '../utils/gtag'

Router.events.on('routeChangeComplete', url => gtag.pageview(url))

const lightTheme = () => ({
  ...themes['common'],
  ...themes['light'],
})

const darkTheme = () => ({
  ...themes['common'],
  ...themes['dark'],
})

const ThemeIcon = css`
  width: 4rem;
  height: 4rem;
  margin-left: auto;
  cursor: pointer;
  @media (max-width: 768px) {
    width: 3rem;
    height: 3rem;
  }
`

const MoonIcon = styled(MoonSVG)`
  ${ThemeIcon}
  color: lightblue;
`

const SunIcon = styled(SunSVG)`
  ${ThemeIcon}
`


export default class MyApp extends App {

  static async getInitialProps({ Component, ctx }) {
    let pageProps = {}
    if (Component.getInitialProps) {
      pageProps = await Component.getInitialProps(ctx);
    }
    return { pageProps };
  }

  state = {
    theme: themes['light'],
    isHeb: true,
  }

  componentWillMount() {
    this.setLightTheme()
  }

  changeLang =    () => this.setState(prevState => ({ ...prevState, isHeb: !isHeb }))
  setDarkTheme =  () => this.setState(prevState => ({ ...prevState, theme: darkTheme() }))
  setLightTheme = () => this.setState(prevState => ({ ...prevState, theme: lightTheme() }))

  render() {

    // const isLight = this.theme.type === 'light'
    const isLight = true
    const { Component, pageProps } = this.props
    const themeButton = isLight && <MoonIcon onClick={this.setDarkTheme} /> || <SunIcon onClick={this.setLightTheme} />

    console.log(this.theme);


    return (
      <ThemeProvider theme={this.theme}>
        <Header title='C19.FeeD' isHeb={this.state.isHeb}>
          { themeButton }
        </Header>
        <Component {...pageProps} />
      </ThemeProvider>
    )
  }
}
