import styled from 'styled-components'
import { Container } from './S'


const Column = styled.div`
  width: 100%;
  box-shadow: ${props => props.theme.shadows[1]};
  border-radius: 2px;
  background: #fafafa;
  height: 84px;
`

const InRow = styled.div`
  display: flex;
  flex-flow: row wrap;
  font-weight: 700;
  color: ${props => props.theme.primaryColor};
  justify-content: ${props => props.justify || 'space-between'};
`

const Header = styled.div`
  font-size: 20px;
  font-weight: 500;
  color: ${props => props.theme.black};
  width: auto;
`

const Number = styled.h1`
  font-size: 22px;
  font-weight: 700;
  color: ${props => props.theme.primaryColor};
`

const Row = styled.div`
  display: flex;
  flex-wrap: wrap;
  margin-right: -15px;
  margin-left: -15px;
`

const Col = styled.div`
  text-align: center;
  padding: 8px 10px;
  font-weight: 400;
  flex: 1 1 20%;
  max-width: 20%;
  position: relative;
  width: 100%;
  display: flex;
  flex-direction: column;
  background: ${props => (props.bg && props.theme.general[props.bg]) || props.theme.primaryColor};
  color: ${props => props.theme.onPrimary};
  justify-content: center;
  @media (max-width: 768px) {
    padding: 8px 16px;
  }
`

const Country = styled.h3`
  font-size: 16px;
  font-weight: bold;
  align-self: center;
  color: ${props => props.theme.onPrimary};
  @media (max-width: 768px) {
    font-size: 12px;
  }
`

const Div = styled.div`
  box-shadow: ${props => props.theme.shadows[2]};
  border-radius: 6px;
  margin-bottom: 16px;
`

const isPercentDown = x => x < 0
const showPercent = x => x !== 0

export default ({ S, country, isHeb}) => {

  return (
    <Container>


    <Column>

      <Header>
        {isHeb ? 'מאומתים' : 'Confirmed'}
      </Header>
      <InRow>
        <Number>{S.cases}</Number>
    <br />
        {showPercent(S.todayDiff) && S.todayDiff + '%'}
      </InRow>
    </Column>


      <Div>
        <Row>
          <Col style={{ borderRight: 'none', borderTopRightRadius: '5px', borderBottomRightRadius: '5px' }}>
            <Country> {country} </Country>
          </Col>
          <Col bg='successDefault' >
            <Country>
              <small>{isHeb ? 'מאומתים' : 'Confirmed'}</small>
              <br />
              {S.cases}
            </Country>
          </Col>
          <Col bg='analogous'>
            <Country>
              <small>{isHeb ? 'החלימו' : 'Recovered'}</small>
              <br />
              {S.recovered}
            </Country>
          </Col>
          <Col bg='error'>
            <Country>
              <small>{ isHeb ? 'נפטרו' : 'Deaths' }</small>
              <br />
              {S.deaths}
          </Country>
          </Col>
          <Col bg='error2' style={{ borderLeft: 'none', borderTopLeftRadius: '5px', borderBottomLeftRadius: '5px' }}>
            <Country>
              <small>{isHeb ? 'מקרים חדשים' : 'Today Cases'}</small>
              <br />
              {S.todayCases}
          </Country>
          </Col>
        </Row>

      </Div>
    </Container>
  )

}
