import {
  useState,
  useEffect,
  useRef,
  createContext
} from 'react'


export const LocaleContext = createContext()

export const LocaleProvider = (props) => {
  // Default Setting
  const initialState = 'he'

  const [locale, setLoc] = useState(initialState)
  const prev = useRef()


  useEffect(() => {
    if (locale === 'he') {
        const langName = JSON.parse(localStorage.getItem('locale'))
        if (langName) {
          console.log('reading {LOCALE} to storage');
          prev.current = langName
          setLoc(locale)
        }
    } else if (prev.langName !== locale) {
      console.log('writing {LOCALE} to storage');
      localStorage.setItem('locale', JSON.stringify(locale))
    }
  }, [locale])

  return (
    <LocaleContext.Provider value={{ locale, setLocale: (value) => setLoc({ ...locale, locale: value  }) } }>
      {props.children}
    </LocaleContext.Provider>
  )
}
