import styled, { withTheme } from 'styled-components'


const Cell = styled.td`
  border: 2px solid ${props => props.theme.error2 + '33'};
  padding: 12px 8px;
  font-size: 14px;
  font-family: 'Rubik';
  background: ${props => props.theme.analogous};
  color: ${props => props.theme[props.color] || props.theme.onPrimary};
  font-weight: 500;
`

const CellHeader = styled.th`
  font-size: 14px;
  font-weight: 500;
  border-color: 1px solid ${props => props.theme.text.primary};
  box-shadow: ${props => props.theme.shadows[1]};
  padding: 12px 8px;
  position: sticky;
  top: 0;
  background: ${props => props.theme.primaryLight};
  color: ${props => props.theme.onPrimary};
`



export default ({ content, header, cellColor }) => {

  const cellMarkup = header ? (
    <CellHeader>
      {content}
    </CellHeader>
  ) : (
    <Cell color={cellColor}>
      {content}
    </Cell>
  );

  return (cellMarkup);
}
