import styled from 'styled-components'
import { Container } from './S'
import { DownIcon, UpIcon } from './Icons/ThemeIcon'

const formatNumber = x => x
  .toString()
  .split('')
  .reverse()
  .map((x,i) => i % 3 === 0 ? x + ',' : x)
  .reverse()
  .join('')
  .slice(0, -1)

const Column = styled.div`
  display: flex;
  flex-flow: column wrap;
  direction: rtl;
  align-items: start;
  justify-content: space-evenly;

  width: 100%;
  background: #fafafa;
  height: 84px;

  padding: 8px 2em 4px;
  margin-bottom: 2em;

  border-radius: 2px;
  box-shadow: ${props => props.theme.shadows[2]};

  @media (min-width: 660px) {
    box-shadow: none;
    border: 1px solid #e4e7eb;
  }

`

const InRow = styled.div`
  display: flex;
  flex-flow: row wrap;
  font-weight: 700;
  color: ${props => props.theme.primaryColor};
  justify-content: ${props => props.justify || 'space-between'};
  width: 100%;
  line-height: 1.65;
`

const TheRow = styled.div`
  display: flex;
  flex-flow: column wrap;
  justify-content: space-between;
  @media (min-width: 660px) {
    flex-flow: row;
  }
`

const Percentage = styled.h1`
  font-size: 14px;
  font-weight: 500;
  color: ${props => props.theme.text.secondary};
  align-self: flex-end;
  padding-bottom: 2px;
  border-radius: 6px;
  background: ${props => props.theme.text.disabled};
`

const Header = styled.div`
  font-size: 18px;
  font-weight: 500;
  color: ${props => props.theme.text.primary};
  width: auto;
  letter-spacing: 0.1px;
`

const Number = styled.h1`
  font-size: 24px;
  font-weight: 500;
  color: ${props => props.theme.primaryVariant};
  letter-spacing: 1.2px;
  font-family: 'Montserrat';

  span {
    font-size: 12px;
    font-family: 'Rubik';
    margin-left: 2em;
    margin-right: 2em;
  }
`

const Row = styled.div`
  display: flex;
  flex-wrap: wrap;
  margin-right: -15px;
  margin-left: -15px;
`

const Col = styled.div`
  text-align: center;
  padding: 8px 10px;
  font-weight: 400;
  flex: 1 1 20%;
  max-width: 20%;
  position: relative;
  width: 100%;
  display: flex;
  flex-direction: column;
  background: ${props => (props.bg && props.theme.general[props.bg]) || props.theme.primaryColor};
  color: ${props => props.theme.onPrimary};
  justify-content: center;
  @media (max-width: 768px) {
    padding: 8px 16px;
  }
`

const Country = styled.h3`
  font-size: 16px;
  font-weight: bold;
  align-self: center;
  color: ${props => props.theme.onPrimary};
  @media (max-width: 768px) {
    font-size: 12px;
  }
`

const Div = styled.div`
  box-shadow: ${props => props.theme.shadows[2]};
  border-radius: 6px;
  margin-bottom: 16px;
`

const isPercentDown = x => x < 0
const showPercent = x => x !== 0

export default ({ S, country, isHeb, yday}) => {

  return (
    <Container>


    <TheRow>

      <Column>
        <Header>
          {isHeb ? 'מאומתים' : 'Confirmed'}
        </Header>
        <InRow>
          <Number>{formatNumber(S.cases)}</Number>
          <br />
          <Percentage>
        </Percentage>
        </InRow>
      </Column>

    <Column>
      <Header>
        {isHeb ? 'החלימו' : 'Recovered'}
      </Header>
      <InRow>
        <Number>{formatNumber(S.recovered)}</Number>
        <br />
        <Percentage>
      </Percentage>
      </InRow>
    </Column>

    <Column>
      <Header>
        {isHeb ? 'נפטרו' : 'Deaths'}
      </Header>
      <InRow>
        <Number>
          {formatNumber(S.deaths)}
          <span style={{ fontSize: '10px' }}>לעומת: {formatNumber(yday.deaths)} מאתמול</span>
        </Number>
        <br />
        <Percentage>
      </Percentage>
      </InRow>
    </Column>

    <Column>
      <Header>
        {isHeb ? 'מקרים חדשים' : 'Today Cases'}
      </Header>
      <InRow>
        <Number>{formatNumber(S.todayCases)}</Number>
        <br />
        <Percentage>
          <span style={{ color: 'red', fontSize: '12px' }} >&#11014;</span>
          {showPercent(S.todayDiff) && +S.todayDiff + '%'}
      </Percentage>
      </InRow>
    </Column>


    </TheRow>


      <Div>
        <Row>
          <Col style={{ borderRight: 'none', borderTopRightRadius: '5px', borderBottomRightRadius: '5px' }}>
            <Country> {country} </Country>
          </Col>
          <Col bg='successDefault' >
            <Country>
              <small>{isHeb ? 'מאומתים' : 'Confirmed'}</small>
              <br />
              {S.cases}
            </Country>
          </Col>
          <Col bg='analogous'>
            <Country>
              <small>{isHeb ? 'החלימו' : 'Recovered'}</small>
              <br />
              {S.recovered}
            </Country>
          </Col>
          <Col bg='error'>
            <Country>
              <small>{ isHeb ? 'נפטרו' : 'Deaths' }</small>
              <br />
              {S.deaths}
          </Country>
          </Col>
          <Col bg='error2' style={{ borderLeft: 'none', borderTopLeftRadius: '5px', borderBottomLeftRadius: '5px' }}>
            <Country>
              <small>{isHeb ? 'מקרים חדשים' : 'Today Cases'}</small>
              <br />
              {S.todayCases}
          </Country>
          </Col>
        </Row>

      </Div>
    </Container>
  )

}
