  const text = isHeb && hebHeader || engHeader


  useEffect(async () => {
    const { data } = await axios.get('https://corona.lmao.ninja/countries/')
    console.log(data);
  }, [])

  // if ( loading ) return <div> Loading Contents . . .</div>

    // data && setLoading( false ) 

  const headers = [ 'country', 'cases', 'active', 'recovered', 'critical', 'deaths', 'todayDeaths', 'todayCases', 'casesPerOneMillion', 'deathsPerOneMillion', 'tests']

  // <Table headings={headers} rows={data} />
  return (
    <>
      <HeaderDescription txt={text} />
    </>
  )

}

