import styled from 'styled-components'

const Text = styled.p`
  color: ${props => props.theme.color};
  font-size: 14px;
  font-family: 'Open Sans';
`

const Wrap = styled.div`
  display: block;
  width: 620px;
  max-width: 620px;
  text-align: left;
  margin: 0 auto 24px;
  padding: 2px;
`

export default ({ children }) => (
  <Wrap>
    <Text>{children}</Text>
  </Wrap>
)
