import React from 'react'
import styled from 'styled-components'
import Container from './Container'
// import Logo from '../assets/icons/logo_transparent.png'

const Wrap = styled.div`
  direction: ltr;
  position: relative;
  height: 82px;
  width: 100%;
  background: linear-gradient(45deg, #5c0434, #000000d6);
  border-bottom: 1px solid darkblue;
  margin: 0 auto;
  margin-bottom: 48px;
`

const Header = styled.header`
  display: flex;
  justify-content;
`

const Nav = styled.nav`
  display: flex;
  align-items: center;
  width: 100%;
`

const List = styled.ul`
  display: flex;
  flex-flow: row wrap;
  justify-content: space-between;
`

const Item = styled.li`
  margin: 0 20px;
`

const Logo = styled.div`
  width: 34px;
  height: 34px;
`

const Side = styled.div`
  display: flex;
  flex-flow: ${props => props.column ? 'column' : 'row'};
  align-items: center;
`

const Title = styled.h1`
  // align-self: center;
  color: #e6e6e6;
  font-size: 2.5rem;
  font-family: monospace;
  text-align: center;
  text-shadow: 0 1px 35px rgba(5, 35, 10, 0.2);
`


export default ({ title, children }) => (
  <Wrap>
    <Container mw='880px'>
      <Header>
        <Nav>
          <Title>{title}</Title>
          <List>
            <Item>/Buy Me A Coffee</Item>

            <Side>
              { children }
            </Side>

          </List>
        </Nav>
      </Header>
    </Container>
  </Wrap>
)
