import styled from 'styled-components'

const Input = styled.input`
  outline: none;
  max-width: 720px;
  width: ${props => props.btn ? '62px' : '85%'};
  text-align: center;
  border-radius: 6px;
  background: ${props => props.theme.categoryColor};
  font-size: 1.1rem;
  padding: 8px 4px;
`
export default (props) => (
    <Input {...props}/>
)
