import useStats from '../utils/useStats';

// Seems like it's not UP TO DATE
// fetch('https://covid19.mathdro.id/api/countries/Italy')


const StatBlock = ({ title, data }) => {
  return (
    <div className="statBlock">
      <h3>{title}:</h3>
      <span>{data}</span>
    </div>
  )
}

function Stats() {
  const stats = useStats();

  if (!stats) return <div>Loading...</div>

  const {
    cases,
    recovered,
    deaths,
    todayCases,
    critical
  } = stats

  console.log(stats)
  return (
    <div>
      <StatBlock 
        title="Infected"
        data={cases}
      />
      <StatBlock 
        title="Recovered"
        data={recovered}
      />
      <StatBlock 
        title="Total Deaths"
        data={deaths}
      />
      <StatBlock 
        title="Critical Cases"
        data={critical}
      />
      <StatBlock 
        title="New Cases Today"
        data={todayCases}
      />
    </div>
  )
}

export default function IndexPage() {
  return (
    <div>
      <Stats />
    </div>
  )
}
